import grid from 'ngraph.generators'

function grid_graph(num_nodes=1000, type='triangular') {
    // alert('here we go!')
    n = Math.log.sqrt
    let graph = require('ngraph.generators').grid(20,10)
    // alert('nodes: '+graph.getNodeCount())
    let nodes = []
    graph.forEachNode(function(node) {nodes.push({id:node.id, label:node.id})})
    let edges = []
    graph.forEachLink(function(link) {edges.push({from:link.fromId, to:link.toId})})
    return {nodes: nodes, edges: edges}
}

export default grid_graph;
