function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}

function generateGraph (nodes, grid, scheme) {
  // let data = {
  //   nodes: [
  //     { id: 1, label: "Node 1", title: "node 1 tootip text" },
  //     { id: 2, label: "Node 2", title: "node 2 tootip text" },
  //     { id: 3, label: "Node 3", title: "node 3 tootip text" },
  //     { id: 4, label: "Node 4", title: "node 4 tootip text" },
  //     { id: 5, label: "Node 5", title: "node 5 tootip text" }
  //   ],
  //   edges: [
  //     { from: 1, to: 2 },
  //     { from: 1, to: 3 },
  //     { from: 2, to: 4 },
  //     { from: 4, to: 2 },
  //     { from: 2, to: 1 },
  //     { from: 2, to: 5 }
  //   ]
  // };
  let data = null
  if(grid == 'SQR') {
    data = grid_graph(nodes, 'square')
    // return data;
  } else if(grid == 'BIN') {
    data = bin_graph(nodes, 'square')
    // return data;
  } else {
    alert('not implemented error!')
    return {nodes : [], edges : []}
  }


  if(scheme == 'None') {
    return data;
  }
  else if(scheme == 'N5') {
    let n = data.nodes.length
    let rs = n / 20
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.nodes.length);
      const el = data.nodes.splice(random, 1)[0];
      data.edges.forEach(element => {
        if(element.from == el || element.to == el) {
          const index = data.egdes.indexOf(element)
          data.edges.splice(index, 1)
        }
      });
      console.log(el);
    }
  } else if(scheme == 'E5') {
    let n = data.edges.length
    let rs = n / 20

  // alert(data.nodes.length + " " + n + " " + rs)
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.edges.length);
      const el = data.edges.splice(random, 1)[0];
      alert(el.from + " " + el.to)
    }
  }
  else if(scheme == 'N10') {
    let n = data.nodes.length
    let rs = n / 20
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.nodes.length);
      const el = data.nodes.splice(random, 1)[0];
      data.edges.forEach(element => {
        if(element.from == el || element.to == el) {
          const index = data.egdes.indexOf(element)
          data.edges.splice(index, 1)
        }
      });
      console.log(el);
    }
  } else if(scheme == 'E10') {
    let n = data.edges.length
    let rs = n / 10

  // alert(data.nodes.length + " " + n + " " + rs)
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.edges.length);
      const el = data.edges.splice(random, 1)[0];
      alert(el.from + " " + el.to)
    }
  }
  else if(scheme == 'N20') {
    let n = data.nodes.length
    let rs = n / 5
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.nodes.length);
      const el = data.nodes.splice(random, 1)[0];
      data.edges.forEach(element => {
        if(element.from == el || element.to == el) {
          const index = data.egdes.indexOf(element)
          data.edges.splice(index, 1)
        }
      });
      console.log(el);
    }
  } else if(scheme == 'E20') {
    let n = data.edges.length
    let rs = n / 5

  // alert(data.nodes.length + " " + n + " " + rs)
    while(rs > 0) {
      rs -= 1
      const random = Math.floor(Math.random() * data.edges.length);
      const el = data.edges.splice(random, 1)[0];
      alert(el.from + " " + el.to)
    }
  }
  else {
    alert('not implemented error!')
    return data;
  }

  return data
}


const App = () => {



  // load the JSON file containing the Gephi network.
  // try {
  //   var gephiJSON = loadJSON("./datasources/graph_data.json"); // code in importing_from_gephi.

  // // you can customize the result like with these options. These are explained below.
  // // These are the default options.
  // var parserOptions = {
  //   edges: {
  //     inheritColor: false
  //   },
  //   nodes: {
  //     fixed: true,
  //     parseColor: false
  //   }
  // }

  // // parse the gephi file to receive an object
  // // containing nodes and edges in vis format.
  // var parsed = vis.parseGephiNetwork(gephiJSON, parserOptions);

  // // provide data in the normal fashion
  // var data = {
  //   nodes: parsed.nodes,
  //   edges: parsed.edges
  // };

  // } catch {
    // let data = {
    //   nodes: [
    //     { id: 1, label: "Node 1", title: "node 1 tootip text" },
    //     { id: 2, label: "Node 2", title: "node 2 tootip text" },
    //     { id: 3, label: "Node 3", title: "node 3 tootip text" },
    //     { id: 4, label: "Node 4", title: "node 4 tootip text" },
    //     { id: 5, label: "Node 5", title: "node 5 tootip text" }
    //   ],
    //   edges: [
    //     { from: 1, to: 2 },
    //     { from: 1, to: 3 },
    //     { from: 2, to: 4 },
    //     { from: 2, to: 5 }
    //   ]
    // };
  // }


  const createNode = () => {
    // const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      // alert('Nodes: ' + nodes.length)
      const from_pos = Math.floor(Math.random() * (nodes.length - 1));

      const from = nodes[from_pos].id;
      // graph.fit();
      return {
        graph: {
          nodes: [
            ...nodes,
            { id, label: `Node ${id}`}
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }


  const [deleteButton, setDeleteButton] = useState(false)
  const [status, setStatus] = useState('GEN')
  const [state, setState] = useState({
    counter: 5,
    graph: {
      nodes: [
        // { id: 1, label: "Node 1", title: "node 1 tootip text" },
        // { id: 2, label: "Node 2", title: "node 2 tootip text" },
        // { id: 3, label: "Node 3", title: "node 3 tootip text" },
        // { id: 4, label: "Node 4", title: "node 4 tootip text" },
        // { id: 5, label: "Node 5", title: "node 5 tootip text" }
      ],
      edges: [
        // { from: 1, to: 2 },
        // { from: 1, to: 3 },
        // { from: 2, to: 4 },
        // { from: 2, to: 5 }
      ]
    },
    events: {}
  })
  const { graph, events } = state;
  const [nodes, setNodes] = useState(200)
  const [network, setNetwork] = useState(null);

  const handleNodeDelete = () => {
    network.deleteSelected();

    // alert('Nodes: ' + graph.nodes.remove)
    setDeleteButton(false);

  };
  const handleGenerateGraph = (nodes, grid, scheme) => {
    let graph_ = generateGraph(nodes, grid, scheme);
    // alert(graph_.nodes.length)
    setState(({graph, counter, ...rest})=>{return {graph: graph_, counter: graph_.nodes.length, ...rest}})

    setStatus('GEN')
  }


  const [algo, setAlgo] = useState(null)
  const handleAlgoChange = (event) => {
    setAlgo(event.target.value);
    setStatus('RUN')
  };

  const handleStatusChange = (event, value) => {
    setStatus(value)
  }
  const algos = [{label:"Depth First Search",value:"DFS"},{label:"Breadth First Search",value:"BFS"}]
  return (
    <div  //style={{display:"flex"}}>
    >
      <Header value={status} onChange={handleStatusChange}/>
      {/* <SubHeader value={status} onChange={handleStatusChange}/> */}
      {status == 'GEN' && <GraphGenDialog nodes={200} onGenerate={handleGenerateGraph}/>}
      {/* {<menubar name='header' >
        <button onClick={()=>{createNode();}}>
          Click to add random nodes
        </button>
        <button onClick={()=>{}}>
          Save Graph
        </button>
        {deleteButton && <button onClick={handleNodeDelete}>
          Delete
        </button>}
      </menubar>} */}
      {state.graph.nodes.length > 0 && <GraphBox graph={graph} style={{ height: '800px' }} getNetwork={network => {
        setNetwork(network);
      }}/>}
      {state.graph.nodes.length == 0 && <Typography style={{flex: 1, height: '500px', width: '100%', textAlign:'center', textAlignVertical:'middle', paddingTop:'200px'}}>No graph generated yet!</Typography>}
      {/* <Graph graph={graph} options={options} events={events} style={{ height: '800px' }} getNetwork={network => {
        setNetwork(network);
      }}/> */}
      <AlgorithmSelector  value={algo} onChange={handleAlgoChange} algos={algos} position="absolute" bottom="20px"/>
    </div>
  );

}

