import './index.css';
// import App from './App';
import reportWebVitals from './reportWebVitals';

import logo from './logo.svg';
import './App.css';



import Graph from "react-graph-vis";
// import {loadJSON} from "vis-network"
import React, { useState } from "react";
import ReactDOM from "react-dom";
import vis from "vis-network"
import AlgorithmSelector from './components/AlgorithmSelector';
import GraphGenDialog from './components/GraphGenDialog'
import grid_graph from './algorithms/graph_gen/grid'
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import GraphBox from './components/GraphBox';
import { Typography } from '@mui/material';

// function loadJSON(path, success, error) {
//   var xhr = new XMLHttpRequest();
//   xhr.onreadystatechange = function () {
//     if (xhr.readyState === 4) {
//       if (xhr.status === 200) {
//         success(JSON.parse(xhr.responseText));
//       }
//       else {
//         error(xhr);
//       }
//     }
//   };
//   xhr.open('GET', path, true);
//   xhr.send();
// }



function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}

function generateGraph () {
  // let data = {
  //   nodes: [
  //     { id: 1, label: "Node 1", title: "node 1 tootip text" },
  //     { id: 2, label: "Node 2", title: "node 2 tootip text" },
  //     { id: 3, label: "Node 3", title: "node 3 tootip text" },
  //     { id: 4, label: "Node 4", title: "node 4 tootip text" },
  //     { id: 5, label: "Node 5", title: "node 5 tootip text" }
  //   ],
  //   edges: [
  //     { from: 1, to: 2 },
  //     { from: 1, to: 3 },
  //     { from: 2, to: 4 },
  //     { from: 4, to: 2 },
  //     { from: 2, to: 1 },
  //     { from: 2, to: 5 }
  //   ]
  // };
  let data = grid_graph(1000, 'square')
  return data;
}


const App = () => {



  // load the JSON file containing the Gephi network.
  // try {
  //   var gephiJSON = loadJSON("./datasources/graph_data.json"); // code in importing_from_gephi.

  // // you can customize the result like with these options. These are explained below.
  // // These are the default options.
  // var parserOptions = {
  //   edges: {
  //     inheritColor: false
  //   },
  //   nodes: {
  //     fixed: true,
  //     parseColor: false
  //   }
  // }

  // // parse the gephi file to receive an object
  // // containing nodes and edges in vis format.
  // var parsed = vis.parseGephiNetwork(gephiJSON, parserOptions);

  // // provide data in the normal fashion
  // var data = {
  //   nodes: parsed.nodes,
  //   edges: parsed.edges
  // };

  // } catch {
    // let data = {
    //   nodes: [
    //     { id: 1, label: "Node 1", title: "node 1 tootip text" },
    //     { id: 2, label: "Node 2", title: "node 2 tootip text" },
    //     { id: 3, label: "Node 3", title: "node 3 tootip text" },
    //     { id: 4, label: "Node 4", title: "node 4 tootip text" },
    //     { id: 5, label: "Node 5", title: "node 5 tootip text" }
    //   ],
    //   edges: [
    //     { from: 1, to: 2 },
    //     { from: 1, to: 3 },
    //     { from: 2, to: 4 },
    //     { from: 2, to: 5 }
    //   ]
    // };
  // }


  const createNode = () => {
    // const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      // alert('Nodes: ' + nodes.length)
      const from_pos = Math.floor(Math.random() * (nodes.length - 1));

      const from = nodes[from_pos].id;
      // graph.fit();
      return {
        graph: {
          nodes: [
            ...nodes,
            { id, label: `Node ${id}`}
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }


  const [deleteButton, setDeleteButton] = useState(false)
  const [status, setStatus] = useState('INIT')
  const [state, setState] = useState({
    counter: 5,
    graph: {
      nodes: [
        // { id: 1, label: "Node 1", title: "node 1 tootip text" },
        // { id: 2, label: "Node 2", title: "node 2 tootip text" },
        // { id: 3, label: "Node 3", title: "node 3 tootip text" },
        // { id: 4, label: "Node 4", title: "node 4 tootip text" },
        // { id: 5, label: "Node 5", title: "node 5 tootip text" }
      ],
      edges: [
        // { from: 1, to: 2 },
        // { from: 1, to: 3 },
        // { from: 2, to: 4 },
        // { from: 2, to: 5 }
      ]
    },
    events: {}
  })
  const { graph, events } = state;
  const [nodes, setNodes] = useState(200)
  const [network, setNetwork] = useState(null);

  const handleNodeDelete = () => {
    network.deleteSelected();

    // alert('Nodes: ' + graph.nodes.remove)
    setDeleteButton(false);

  };
  const handleGenerateGraph = () => {
    generateSpecificGraph()
    setStatus('GEN')
  }

  const generateSpecificGraph = () => {
    let graph_ = generateGraph();
    // alert(graph_.nodes.length)
    setState(({graph, counter, ...rest})=>{return {graph: graph_, counter: graph_.nodes.length, ...rest}})
  }

  const [algo, setAlgo] = useState(null)
  const handleAlgoChange = (event) => {
    setAlgo(event.target.value);
    setStatus('RUN')
  };
  const algos = [{label:"Depth First Search",value:"DFS"},{label:"Breadth First Search",value:"BFS"}]
  return (
    <div  //style={{display:"flex"}}>
    >
      <Header/>
      <SubHeader status={status} onGenerate={handleGenerateGraph}/>
      {/* {status == 'INIT' && <GraphGenDialog nodes={200} onGenerate={generateSpecificGraph}/>} */}
      {/* {<menubar name='header' >
        <button onClick={()=>{createNode();}}>
          Click to add random nodes
        </button>
        <button onClick={()=>{}}>
          Save Graph
        </button>
        {deleteButton && <button onClick={handleNodeDelete}>
          Delete
        </button>}
      </menubar>} */}
      {state.graph.nodes.length > 0 && <GraphBox graph={graph} style={{ height: '800px' }} getNetwork={network => {
        setNetwork(network);
      }}/>}
      {state.graph.nodes.length == 0 && <Typography style={{flex: 1, height: '700px', width: '1200px', align:'center'}}>No graph generated yet!</Typography>}
      {/* <Graph graph={graph} options={options} events={events} style={{ height: '800px' }} getNetwork={network => {
        setNetwork(network);
      }}/> */}
      <AlgorithmSelector  value={algo} onChange={handleAlgoChange} algos={algos} position="absolute" bottom="20px"/>
    </div>
  );

}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
// If you want to start measuring performance in your app, pass a function
