const options = {
  height: "100%",
    width: "100%",
    nodes: {
        shape: "dot",
        size: 16
    },
    layout: {
        hierarchical: false
    },
    physics: {
        // forceAtlas2Based: {
        //     gravitationalConstant: -26,
        //     centralGravity: 0.005,
        //     springLength: 230,
        //     springConstant: 0.18,
        // },
        maxVelocity: 146,
        solver: "forceAtlas2Based",
        // solver: "repulsion",
        timestep: 0.35,
        stabilization: {
            enabled: true,
            iterations: 2000,
            updateInterval: 25,
        },
    },
    edges: {
        color: "#abb4be"
    },
    // interaction: {
    //   zoomView: false
    // }
};

function randomColor() {
  const red = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const green = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  const blue = Math.floor(Math.random() * 256).toString(16).padStart(2, '0');
  return `#${red}${green}${blue}`;
}


const App = () => {



  // load the JSON file containing the Gephi network.
  // try {
  //   var gephiJSON = loadJSON("./datasources/graph_data.json"); // code in importing_from_gephi.

  // // you can customize the result like with these options. These are explained below.
  // // These are the default options.
  // var parserOptions = {
  //   edges: {
  //     inheritColor: false
  //   },
  //   nodes: {
  //     fixed: true,
  //     parseColor: false
  //   }
  // }

  // // parse the gephi file to receive an object
  // // containing nodes and edges in vis format.
  // var parsed = vis.parseGephiNetwork(gephiJSON, parserOptions);

  // // provide data in the normal fashion
  // var data = {
  //   nodes: parsed.nodes,
  //   edges: parsed.edges
  // };

  // } catch {
    let data = {
      nodes: [
        { id: 1, label: "Node 1", title: "node 1 tootip text" },
        { id: 2, label: "Node 2", title: "node 2 tootip text" },
        { id: 3, label: "Node 3", title: "node 3 tootip text" },
        { id: 4, label: "Node 4", title: "node 4 tootip text" },
        { id: 5, label: "Node 5", title: "node 5 tootip text" }
      ],
      edges: [
        { from: 1, to: 2 },
        { from: 1, to: 3 },
        { from: 2, to: 4 },
        { from: 2, to: 5 }
      ]
    };
  // }


  const createNode = () => {
    const color = randomColor();
    setState(({ graph: { nodes, edges }, counter, ...rest }) => {
      const id = counter + 1;
      const from = Math.floor(Math.random() * (counter - 1)) + 1;
      graph.fit();
      return {
        graph: {
          nodes: [
            ...nodes,
            { id, label: `Node ${id}`, color}
          ],
          edges: [
            ...edges,
            { from, to: id }
          ]
        },
        counter: id,
        ...rest
      }
    });
  }
  const [state, setState] = useState({
    counter: data.nodes.size(),
    graph: data,
    events: {
      select: ({ nodes, edges }) => {
        console.log("Selected nodes:");
        console.log(nodes);
        console.log("Selected edges:");
        console.log(edges);
        // alert("Selected node: " + nodes);
      },
      stabilized: () => {
        if (graph) { // Network will be set using getNetwork event from the Graph component
            graph.setOptions({ physics: false }); // Disable physics after stabilization
            graph.fit();
        }
      }



      // doubleClick: ({ nodes, edges }) => {
      //   console.log("Deleted nodes:");
      //   console.log(nodes);
      //   console.log("Deleted edges:");
      //   console.log(edges);

      //   // alert("Selected node: " + nodes);
      // },
      // doubleClick: ({ pointer: { canvas } }) => {
      //   createNode(canvas.x, canvas.y);
      // }
    }
  })
  const { graph, events } = state;
  // const {network, setNetwork} = useState(null);
  return (
    <div height = '100%'>
      <menubar name='header' >
        <button onClick={()=>{createNode();}}>
          Click to add random nodes
        </button>
        {/* <button onClick={()=>{let str = JSON.stringify(graph);}}>
          Save Graph
        </button> */}
        <p>Work area:</p>
      </menubar>
      {/* <Graph graph={graph} options={options} events={events} style={{ height: '800px' }}/> */}
    </div>
  );

}

ReactDOM.render(
  <App />,
  document.getElementById("root")
);
