import { Button } from "@mui/material";
import { useState } from "react";
import { option } from "vis-util/esnext";

//selection dropdown menu to select algorithm
export default function AlgorithmSelector (props) {
    const {network, setStartNode, setGoalNodes} = props
    // algos = ["DFS", "BFS"]
    const algos = [{label:"Depth First Search",value:"DFS"},{label:"Breadth First Search",value:"BFS"}]
    const [algo, setAlgo] = useState('DFS')
    const handleAlgoChange = (event) => {setAlgo(event.target.value)}
    // var ReactRibbon = require('react-ribbon')

    const handleGoalSet = (event) => {

        alert(network.getSelectedNodes())
        setGoalNodes(network.getSelectedNodes())
    }
    const handleStartSet = (event) => {
        alert()
        if(network.getSelectedNodes().length == 1)
        setStartNode(network.getSelectedNodes()[0])
        else {
            alert('Inappropriate selection! please select exactly one node')
        }
    }
    const view = <div>
        {/* <label> Select an algorithm 
            <select value={algo} onChange={handleAlgoChange}>
                {algos.map((option) => (
                    <option value={option.value}>{option.label}</option>
                ))}
            </select>
        </label> */}
        <div>
        <table align='center' width='72%' border={1} sx={{paddingRight:'532px', marginRight:'532px'}} marginRight='532px'>
            <tr>
                <td align="center" colSpan={1}>
                    Set start node
                </td>
                <td align="center" colSpan={1}>
                    Set goal node(s)
                </td>
                <td align="center" colSpan={1}>
                    Select algorithm
                </td>
                {/* <td rowSpan={2} align='center' colSpan={3}>
                    <Button sx={{bgcolor:'primary', width:'100%'}} onClick={handleGenerateGraph}>Custom Algorithm</Button>
                </td> */}
            </tr>
            <tr>
                <td align="center">
                    <Button sx={{bgcolor:'primary', width:'100%'}} onClick={handleStartSet}>Set selected nodes</Button>
                </td>
                <td align="center">
                    <Button sx={{bgcolor:'primary', width:'100%'}} onClick={handleGoalSet}>Set selected nodes</Button>
                </td>
                <td colSpan={1} rowSpan={1} align='center'>
                <select value={algo} onChange={handleAlgoChange}>
                {algos.map((option) => (
                    <option value={option.value}>{option.label}</option>
                ))}
                </select>
                </td>
            </tr>
        </table>

        {/* <p padding='10px'>Select nodes</p> */}

    </div>
    </div>

    return view;
};
